import React, { Component } from "react";
import { connect } from "react-redux";
import ClipLoader from "react-spinners/ClipLoader";
import { getUserProfile } from "../redux/actions/postsActions";
import moment from "moment";
import BlogpostProfile from "../components/BlogpostProfile";

class ProfilePage extends Component {
  state = {
    loading: true,
  };

  setBgColor = color => {
    document.body.style.backgroundColor = color;
  }
  componentDidMount() {
    // console.log(this.props.match.params.id);
    // const { id } = this.props.match.params;

    // API.get(`/api/users/${id}`).then((res) => {
    //   console.log("detail", res.data);
    // });

    const { id } = this.props.match.params;
    this.props.getUsersDetails(id);
    this.setBgColor(this.props.post.userDetails.favorite_color)
    this.setState({
      loading: !this.state.loading,
    });
  }

  render() {
    // const {
    //   avatar,
    //   blog_posts,
    //   comments,
    //   created_at,
    //   favorite_color,
    //   first_name,
    //   last_name,
    //   last_login_at,
    // } = this.props.post.userDetails;

    const {
      userDetails
    } = this.props.post;

    const { loading } = this.props.post;

    console.log("prof page props", this.props.post);

    return (
      <div>
        {loading ? (
          <ClipLoader />
        ) : (
          <div className={`favColor-${userdetails.favorite_color}`}>
            {/* header */}
            <section>
              <div>
                <img src={userdetails.avatar} alt="" />
              </div>
              <div>
                <h2>{`${userdetail.first_name} ${userdetail.last_name}`}</h2>
                <h3>Member since: {moment(userdetail.created_at).format("YYYY-MM-DD")}</h3>
                {/* data totals of user */}
                <div>
                  <div>
                    <h3><span className="badge badge-secondary">{userdetail.comments.length}</span> comments</h3>

                  </div>
                  <div>
                    <h3><span className="badge badge-secondary">{userdetail.blog_posts.length}</span> blog posts</h3>

                  </div>
                </div> {/* end of data */}
              </div>
            </section>

            <section>
              <h2>All blog posts of {userdetail.first_name}</h2>
              { userdetail.blog_posts.map(post => (
                 <BlogpostProfile post={post}/>
              )) }
            </section>
          </div>
        )}
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  post: state.posts
});

const mapDispatchToProps = (dispatch) => ({
  getUsersDetails: (userId) => dispatch(getUserProfile(userId)),
});

export default connect(mapStateToProps, mapDispatchToProps)(ProfilePage);
