import React, { Component } from "react";
import { connect } from "react-redux";
import { fetchPost } from "../redux/actions/postsActions";
import Pagebutton from "../components/Pagebutton";

class Pagination extends Component {
  getToPage = (page) => {
    this.props.getRequestedPage(page);
  };

  render() {
    console.log(this.props);
    const {
      last_page: totalNumPages,
      current_page: current,
    } = this.props.postData;
    console.log(totalNumPages, current);
    const pages = new Array(totalNumPages);

    return (
      <div>
         <Pagebutton prop1="1" prop2="2" />
        <ul>
          {pages.map((el, _i) => (
            <Pagebutton prop1={el} prop2={_i} />
          ))}
          <li>a</li>
          <li>b</li>
          <li>c</li>
          <li>d</li>
        </ul>
        {}
        <button onClick={() => this.getToPage(5)}> page</button>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  postData: state.posts,
});

const mapDispatchToProps = (dispatch) => ({
  getRequestedPage: (page) => dispatch(fetchPost(page)),
});

export default connect(mapStateToProps, mapDispatchToProps)(Pagination);
