import React, { Component } from "react";
import { connect } from "react-redux";
import { fetchPost } from "../redux/actions/postsActions";
import Blogpost from "../components/Blogpost";
import ClipLoader from "react-spinners/ClipLoader";
import CreatePostBtn from "../components/CreatePostBtn";
import { getUser } from "../redux/actions/authActions";
import Pagination from "../components/Pagination";

class Overview extends Component {
  componentDidMount() {
    console.log('props overview on mount', )
    this.props.getPosts(this.props.match.params.page);
    this.props.getUser();
    console.log(this.props.userAuthorized.user)
  }

  render() {
    const { postData, userAuthorized } = this.props;
    console.log(this.props.userAuthorized.user)

    if(postData.posts && postData.posts.length !== 0){
      return <div>
        <h1 className='text-center my-2'>Blogposts overview</h1>
        <div>{userAuthorized.user !== "not set" && <CreatePostBtn />}</div>
          <div>
            {postData.posts.map((post) => (
              <Blogpost postDetail={post} authInfo={userAuthorized}/>
            ))}
          </div>
        <Pagination />
        <button onClick={()=>{ localStorage.setItem("last_visited_page", 6) }}>local</button>
      </div>
    }else{
      return <ClipLoader />
    }
  }
}

const mapStateToProps = (state) => ({
  postData: state.posts,
  userAuthorized: state.auth
});

const mapDispatchToProps = (dispatch) => ({
  getPosts: (page) => dispatch(fetchPost(page)),
  getUser: () => dispatch(getUser),
});

export default connect(mapStateToProps, mapDispatchToProps)(Overview);
