import React, { Component } from "react";
import { connect } from "react-redux";
import ClipLoader from "react-spinners/ClipLoader";

import { getUserProfile } from "../redux/actions/postsActions";

class ProfilePage extends Component {
  state = {
    loading: true
  }
  componentDidMount() {
    // console.log(this.props.match.params.id);
    // const { id } = this.props.match.params;

    // API.get(`/api/users/${id}`).then((res) => {
    //   console.log("detail", res.data);
    // });

    const { id } = this.props.match.params;
    this.props.getUsersDetails(id).then(response=>{
      this.setState({
        loading: !this.state.loading
      })
    }
      );

  }

  render() {
    const {
      avatar,
      blog_posts,
      comments,
      created_at,
      favorite_color,
      first_name,
      last_name,
      last_login_at,
    } = this.props.post.userDetails;

    const { loading } = this.state
    console.log("prof page props",loading);


    return (
    <div>
      {loading ? 'loading' : 'ready'}


      * User profile page <br/>
    * User details  <br/>
    *  Aantal comments (total) (°)  <br/>
    * Aantal posts (total) (°)  <br/>
    * posts van particular user (!!)  <br/>
    * posts waarop particular user heeft gecomment (!!)
    </div>);
  }
}

const mapStateToProps = (state) => ({
  post: state.posts,
});

const mapDispatchToProps = (dispatch) => ({
  getUsersDetails: (userId) => dispatch(getUserProfile(userId)),
});

export default connect(mapStateToProps, mapDispatchToProps)(ProfilePage);
