import React, { Component } from 'react'
import { Redirect } from 'react-router-dom';
import { logUserOut } from "../redux/actions/authActions"
import {connect} from "react-redux"

class Logout extends Component {
    state =Â {
        loggedOut: false
    }

    deleteToken = () => {
        localStorage.removeItem("_DEMO_TOKEN");
        this.setState({
            loggedOut: true
        })

        this.props.logout();

    }

    render() {
        const token = localStorage.getItem("_DEMO_TOKEN")
        return (
            <div>
                 {token ? <button onClick={this.deleteToken} >logout</button> : <Redirect to="/overview/1"/>}
            </div>
        )
    }
}

const mapDispatchToProps = dispatch => ({
    logout: () => dispatch(logUserOut)
})

export default connect(undefined, mapDispatchToProps)(Logout)
