import React, { Component } from "react";
import { connect } from "react-redux";
import ClipLoader from "react-spinners/ClipLoader";
import { getUserProfile } from "../redux/actions/postsActions";
import moment from "moment";
import BlogpostProfile from "../components/BlogpostProfile";

class ProfilePage extends Component {
  state = {
    loading: true,
  };
  componentDidMount() {
    // console.log(this.props.match.params.id);
    // const { id } = this.props.match.params;

    // API.get(`/api/users/${id}`).then((res) => {
    //   console.log("detail", res.data);
    // });

    const { id } = this.props.match.params;
    this.props.getUsersDetails(id);
    this.setState({
      loading: !this.state.loading,
    });
  }

  render() {
    const {
      avatar,
      blog_posts,
      comments,
      created_at,
      favorite_color,
      first_name,
      last_name,
      last_login_at,
    } = this.props.post.userDetails;

    const { loading } = this.props.post;

    console.log("prof page props", this.props.post.userDetails);

    return (
      <div>
        {loading ? (
          <ClipLoader />
        ) : (
          <div>
            {/* header */}
            <section>
              <div>
                <img src={avatar} alt="" />
              </div>
              <div>
                <h2>{`${first_name} ${last_name}`}</h2>
                <h3>Member since: {moment(created_at).format("YYYY-MM-DD")}</h3>
                {/* data totals of user */}
                <div>
                  <div>
                    <h3># comments</h3>
                    <span class="badge badge-secondary">{comments.length}</span>
                  </div>
                  <div>
                    <h3># blog posts</h3>
                    <span class="badge badge-secondary">{blog_posts.length}</span>
                  </div>
                </div> {/* end of data */}
              </div>
            </section>

            <section>
              <h2>All blog posts of {first_name}</h2>
              { blog_posts.map(post => (
                 <BlogpostProfile post={post}/>
              )) }
            </section>
          </div>
        )}
        * User profile page <br />
        * User details <br />
        * Aantal comments (total) (°) <br />
        * Aantal posts (total) (°) <br />
        * posts van particular user (!!) <br />* posts waarop particular user
        heeft gecomment (!!)
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  post: state.posts,
});

const mapDispatchToProps = (dispatch) => ({
  getUsersDetails: (userId) => dispatch(getUserProfile(userId)),
});

export default connect(mapStateToProps, mapDispatchToProps)(ProfilePage);
