import React, { Component } from "react";
import { connect } from "react-redux";
import { fetchPost } from "../redux/actions/postsActions";
import { Link } from "react-router-dom";

class Pagebutton extends Component {
  getToPage = (num) => {
    localStorage.setItem("last_visited_page", num);
    this.props.getPosts(num);
  };

  render() {
    return (
      <div>
        <Link to={`/overview/${this.props.pageNr}`}>
          <button onClick={this.getToPage(this.props.pageNr)}></button>
          {this.props.pageNr}
        </Link>
      </div>
    );
  }
}

const mapDispatchToProps = (dispatch) => ({
  getPosts: (page) => dispatch(fetchPost(page)),
});

export default connect(undefined, mapDispatchToProps)(Pagebutton);
