const initialState = {  user: 'not set', id: 0 }

const authReducer = (state = initialState, action) => {
  const {type, payload} = action;

  switch(type){
    case 'SET_USER':
        return { 
          user: payload }

    case 'LOGOUT_USER':
      return {
        user: 'not set'
      }

    default:
      return state
  }
}

export default authReducer;
