import React, { Component } from 'react'
import {API} from '../libs/API'
import { Formik } from "formik";
import * as Yup from "yup";
import PostTextEditor from "./PostTextEditor";

export default class EditPost extends Component {
    state = {
        title: '',
        body: '',
        loading: true
    }

    componentDidMount(){
        const {idPost: id} = this.props.match.params
        API.get(`/api/posts/${id}`).then(
            response => {
                console.log(response)
                this.setState({
                    title: response.data.title,
                    body: response.data.body
                })
            }
        )
    }


    render() {
        console.log('prps', this.props.match.params)
        console.log(this.state)
        const {title, body} = this.state

        return (
            <div>
                {this.state.title !== '' ? <Formik
          onSubmit={this.handleSubmitCreate}

          initialValues={{
            CREATE_POST_title: this.state.title,
            // D.m.v. deze keys worden values opgehaald in <Field>
            CREATE_POST_editor: this.state.body
          }}
          // Input and give feedback / errors
          validationSchema={Yup.object({})}
        >
          {props => <PostTextEditor {...props} />}
        </Formik>

    :
    ''}

            </div>
        )
    }
}
